import React from "react";
import profile from '../Assests/ProfilePicture-2.png';
import styled from "styled-components";
import { useSpring, animated} from "react-spring";


function GlassCard() {
    const [props, set] = useSpring(() => ({ xys:[0, 0, 1], config: {mass: 10, tension: 200, friction: 50} }))

    const calc = (x, y) => [-(y - window.innerHeight / 2) / 20, (x - window.innerWidth / 2) / 20, 1]
    const trans = (x, y, s) => `perspective(600px) rotateX(${x}deg) rotateY(${y}deg) scale(${s})`

    const Container = styled(animated.div)`
        display: inline-block;
      padding: 3em;
      background: #C7D2FE66;
      border-radius: 10px;
      z-index: 1;
      position: relative;
      backdrop-filter: blur(30px);
      border: 2px solid transparent;
      background-clip: border-box;
      cursor: pointer;
    `;

    const StyledImg = styled.img`
      width: 200px;
      height: auto;
      //border: 2px solid #000;
      //border-radius: 50%;
    `;

    const StyledH1 = styled.h1`
      line-heright: 1.5;
      letter-spacing: 1.5;
      font-family: "Gilroy";

    `;

    const StyledH3 = styled.h3`
      line-heright: 1.5;
      letter-spacing: 1.15;
      font-family: "Gilroy";
      font-size: 20px;
    `;


    return (
        <Container
            onMouseMove={({clientX: x, clientY: y}) => (set({xys: calc(x, y)}))}
            onMouseLeave={() => set({xys: [0,0,1]})}
            style={{
                transform: props.xys.interpolate(trans)
            }}
        >
            <StyledImg src={profile}/>
            <StyledH1>Arjun Bhalodia</StyledH1>
            <StyledH3>Full Stack Engineeer <br/> and Backend Engineer</StyledH3>
        </Container>
    );
}

export default GlassCard;
